                <IonIcon src={barbell}></IonIcon>
                <IonText>Nb. d'exercises: {nbExo}</IonText>
              </IonItem>
              <br />
              <IonItem>
                <IonIcon src={timer}></IonIcon>
                <IonText>Temps exercés: {time}</IonText>
              </IonItem>
              <br />
              <IonItem>
                <IonIcon src={restaurant}></IonIcon>
